widgets.css:
JS{{
var colRAW = OSC{/artColors,0}
//console.log(colRAW);
var colors=colRAW.split(",");
//console.log(colors);

var css = 'value {border-radius:.3em; font-family:"Helvetica"; font-size:140%; line-height:1.25; border:1px solid #000000; text-shadow: .1em .1em .1em #1b1b1b; }\n'
for (var i = 0; i <= colors.length; i++) {
  css += "value:nth-child(" + (i) + "){border-radius:.3em; --color-fill:" + colors[i-1] + "; font-size:140%; border:1px solid #000000; text-shadow:.1em .1em .1em #1b1b1b; }\n"
  css += "value.on:nth-child(" + (i) + "){border-radius:.3em; --color-fill:" + colors[i-1] + "; font-size:160%; border:1px solid #ffffff; text-shadow: .1em .1em .1em #1b1b1b; color: #ffffff; }\n"
}

//style single active state
var curArt = OSC{/artNewInt,0}
//console.log(curArt);

css += "value:nth-child(" + curArt + "){border-radius:.3em; background-color: var(--color-fill); border:1px solid #ffffff; font-size:160%; text-shadow: .1em .1em .1em #1b1b1b; }\n"
css += ".marquee {-webkit-text-size-adjust:none;text-size-adjust:none;position: absolute;height: 100%;width:100%;margin: 0;text-align: left;-moz-transform: translateX(50%);-webkit-transform: translateX(50%);transform: translateX(50%);-moz-animation: bouncing-text 3s linear infinite alternate;-webkit-animation: bouncing-text 3s linear infinite alternate;animation: bouncing-text 10s linear infinite alternate;}@-moz-keyframes bouncing-text {0% {-moz-transform: translateX(50%);}100% {-moz-transform: translateX(-50%);}}@-webkit-keyframes bouncing-text {0% {-webkit-transform: translateX(50%);}100% {-webkit-transform: translateX(-50%);}}@keyframes bouncing-text {0% {-moz-transform: translateX(50%);-webkit-transform: translateX(50%);transform: translateX(50%);}100% {-moz-transform: translateX(-50%);-webkit-transform: translateX(-50%);transform: translateX(-50%);}}\n"

return css
}}

widgets.onValue:
// send button number to MidiPipe for editing articulation on
// individual notes in Logic's piano roll via UI AppleScript
var cc = 110
var channel = 16
var buttonNumber
var values = getProp(this, 'values')
if (Array.isArray(values)) {
  // if values is written as an array (["a", "b"])
  buttonNumber = values.indexOf(value)
} else {
  // if values is written as an object ({"label a": "a", "label b": "b"})
  // +3 to account for Logic menu UI "-"" and divider line
  buttonNumber = Object.values(values).indexOf(value) +3
}
//console.log('value pressed buttonNumber ' + buttonNumber)
send('midi:OSC-Midi', '/control', channel, cc, buttonNumber)

// switch's onValue property
// since the message is sent here we could set bypass to true
// to prevent sending the default messages based on
// address, preArgs and target
var note = value
send('midi:OSC-Midi', '/note', channel, note, 127)
send('midi:OSC-Midi', '/note', channel, note, 0)

// enable multiple taps
setTimeout(reset, 500)
function reset() {
  set("this", -1, {send:false})
}

